mapper.insert:
INSERT INTO
	CATEGORY_ITEM ("asset_category", "asset_item")	
SELECT
	#{assetCategory}, #{assetItem}
WHERE NOT EXISTS(
	SELECT
		"asset_category", "asset_item"				
	FROM
		CATEGORY_ITEM
	WHERE
		ASSET_CATEGORY=#{assetCategory}
		AND ASSET_ITEM=#{assetItem}
)

mapper.delete:
DELETE FROM
	CATEGORY_ITEM
WHERE
	ASSET_ITEM=#{itemName}
	AND ASSET_CATEGORY=#{categoryName}

mapper.update:
UPDATE
	CATEGORY_ITEM
SET
	ASSET_CATEGORY=#{categoryName}
WHERE
	ASSET_CATEGORY=#{categoryOriName};
	
UPDATE
	ASSET_COMMON_ITEM
SET
	ASSET_CATEGORY=#{categoryName}
WHERE
	ASSET_CATEGORY=#{categoryOriName};
	
UPDATE
	CATEGORY_CODE
SET
	ASSET_CATEGORY=#{categoryName}
WHERE
	ASSET_CATEGORY=#{categoryOriName}

UPDATE
	CATEGORY_ITEM
SET
	ASSET_ITEM=#{itemName}
WHERE
	ASSET_ITEM=#{itemOriName}
	AND ASSET_CATEGORY=#{categoryName}
